import React, { useEffect, useState } from "react";
import axios from "axios";

const BACKEND = "http://127.0.0.1:8000"; // ensure backend matches this

export default function App() {
  const [players, setPlayers] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    async function loadPlayers() {
      try {
        const res = await axios.get(`${BACKEND}/players`);
        setPlayers(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load players");
      }
    }
    loadPlayers();
  }, []);

  if (error) return <h2 style={{color:"red"}}>{error}</h2>;
  if (!players.length) return <h2>Loading players...</h2>;

  return (
    <div style={{padding:20, fontFamily:"Arial"}}>
      <h1>Realtime Player Stats</h1>
      {players.map(p => (
        <div key={p.id} style={{
          marginBottom:10,
          padding:10,
          border:"1px solid #ddd",
          borderRadius:6,
          background:"#f8f9fa"
        }}>
          <h3>{p.name} ({p.sport})</h3>
          <div>Team: {p.team}</div>
          <div>Matches: {p.stats.matches}</div>
          <div>{p.sport === "cricket" ? "Runs" : "Goals"}: {p.stats.runs_or_goals}</div>
          <div>Average: {p.stats.average}</div>
        </div>
      ))}
    </div>
  );
}
