import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import { v4 as uuidv4 } from "uuid";

const BACKEND = "http://localhost:8000";

function PlayerCard({ player, onEditClick }) {
  return (
    <div className="card">
      <h3>{player.name} <span className="small">({player.sport})</span></h3>
      <div className="small">Team: {player.team} â€¢ Age: {player.age}</div>
      <hr />
      <div><strong>Matches:</strong> {player.stats.matches}</div>
      <div><strong>{player.sport === "cricket" ? "Runs" : "Goals"}:</strong> {player.stats.runs_or_goals}</div>
      <div><strong>Average:</strong> {player.stats.average}</div>
      <div className="small">Other: {JSON.stringify(player.stats.other)}</div>
      <div style={{marginTop:8}}>
        <button onClick={() => onEditClick(player)}>Edit</button>
      </div>
    </div>
  );
}

function AddPlayerForm({ onAdded }) {
  const [name, setName] = useState("");
  const [sport, setSport] = useState("cricket");
  const [team, setTeam] = useState("");
  const [age, setAge] = useState(25);
  const [matches, setMatches] = useState(0);
  const [runs, setRuns] = useState(0);
  const [avg, setAvg] = useState(0.0);

  async function submit(e) {
    e.preventDefault();
    const payload = {
      name, sport, team, age,
      stats: {
        matches: Number(matches),
        runs_or_goals: Number(runs),
        average: Number(avg),
        other: {}
      }
    };
    const res = await axios.post(`${BACKEND}/players`, payload);
    setName(""); setTeam(""); setAge(25); setMatches(0); setRuns(0); setAvg(0);
    onAdded(res.data);
  }

  return (
    <form className="form" onSubmit={submit}>
      <input required placeholder="Name" value={name} onChange={e => setName(e.target.value)} />
      <select value={sport} onChange={e => setSport(e.target.value)}>
        <option value="cricket">Cricket</option>
        <option value="football">Football</option>
      </select>
      <input placeholder="Team" value={team} onChange={e => setTeam(e.target.value)} />
      <input type="number" placeholder="Age" value={age} onChange={e => setAge(e.target.value)} />
      <input type="number" placeholder="Matches" value={matches} onChange={e => setMatches(e.target.value)} />
      <input type="number" placeholder={sport === "cricket" ? "Runs" : "Goals"} value={runs} onChange={e => setRuns(e.target.value)} />
      <input type="number" step="0.01" placeholder="Average" value={avg} onChange={e => setAvg(e.target.value)} />
      <button type="submit">Add Player</button>
    </form>
  );
}

function EditModal({ player, onClose, onSaved }) {
  const [matches, setMatches] = useState(player ? player.stats.matches : 0);
  const [runs, setRuns] = useState(player ? player.stats.runs_or_goals : 0);
  const [avg, setAvg] = useState(player ? player.stats.average : 0);

  useEffect(() => {
    if (player) {
      setMatches(player.stats.matches);
      setRuns(player.stats.runs_or_goals);
      setAvg(player.stats.average);
    }
  }, [player]);

  async function save() {
    const payload = {
      matches: Number(matches),
      runs_or_goals: Number(runs),
      average: Number(avg),
      other: player.stats.other || {}
    };
    await axios.patch(`${BACKEND}/players/${player.id}/stats`, payload);
    onSaved();
    onClose();
  }

  if (!player) return null;
  return (
    <div style={{
      position:"fixed", left:0, top:0, right:0, bottom:0,
      display:"flex", justifyContent:"center", alignItems:"center",
      background:"rgba(0,0,0,0.35)"
    }}>
      <div style={{background:"white", padding:20, borderRadius:8, width:400}}>
        <h3>Edit {player.name}</h3>
        <div style={{display:"flex", gap:8, flexDirection:"column"}}>
          <label>Matches<input type="number" value={matches} onChange={e=>setMatches(e.target.value)} /></label>
          <label>{player.sport === "cricket" ? "Runs" : "Goals"}<input type="number" value={runs} onChange={e=>setRuns(e.target.value)} /></label>
          <label>Average<input type="number" step="0.01" value={avg} onChange={e=>setAvg(e.target.value)} /></label>
          <div style={{display:"flex", gap:8, justifyContent:"flex-end"}}>
            <button onClick={onClose} style={{background:"#999"}}>Cancel</button>
            <button onClick={save}>Save</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default function App(){
  const [players, setPlayers] = useState([]);
  const [filterSport, setFilterSport] = useState("all");
  const [editing, setEditing] = useState(null);
  const wsRef = useRef(null);

  useEffect(() => {
    async function fetchAll() {
      const res = await axios.get(`${BACKEND}/players`);
      setPlayers(res.data);
    }
    fetchAll();

    // Setup websocket
    const proto = window.location.protocol === "https:" ? "wss" : "ws";
    const wsUrl = `${proto}://localhost:8000/ws`;
    const ws = new WebSocket(wsUrl);
    wsRef.current = ws;

    ws.onopen = () => console.log("WS connected");
    ws.onmessage = (evt) => {
      const data = JSON.parse(evt.data);
      // handle messages: init, player_created, player_updated, stats_updated, player_deleted
      if (data.type === "init") {
        setPlayers(data.players);
      } else if (data.type === "player_created") {
        setPlayers(prev => [...prev, data.player]);
      } else if (data.type === "player_updated" || data.type === "stats_updated") {
        setPlayers(prev => prev.map(p => p.id === data.player.id ? data.player : p));
      } else if (data.type === "player_deleted") {
        setPlayers(prev => prev.filter(p => p.id !== data.player_id));
      }
    };
    ws.onclose = () => console.log("WS closed");
    ws.onerror = (e) => console.error("WS error", e);

    return () => {
      try { ws.close(); } catch(e){}
    };
  }, []);

  function onAdded(newPlayer) {
    // server will broadcast creation; optional optimistic add removed
    console.log("added", newPlayer);
  }

  function onEditClick(player) {
    setEditing(player);
  }

  function onSaved() {
    // saved; server will broadcast updated player
  }

  const visible = players.filter(p => filterSport === "all" ? true : p.sport === filterSport);

  return (
    <div className="app">
      <div className="header">
        <h1>Realtime Player Stats</h1>
        <div>
          <label className="small">Filter: </label>
          <select value={filterSport} onChange={e=>setFilterSport(e.target.value)}>
            <option value="all">All</option>
            <option value="cricket">Cricket</option>
            <option value="football">Football</option>
          </select>
        </div>
      </div>

      <AddPlayerForm onAdded={onAdded} />

      <div className="grid">
        {visible.map(p => (
          <PlayerCard key={p.id} player={p} onEditClick={onEditClick} />
        ))}
      </div>

      <EditModal player={editing} onClose={() => setEditing(null)} onSaved={onSaved} />
    </div>
  );
}
